const baseConfig={buttonSelector:"[data-show-password]",inputSelector:"[data-input-password]"};class ShowPasswordManager{constructor(n=baseConfig){this.config=n;this.buttons=document.querySelectorAll(n.buttonSelector);this.buttons&&this.buttons.forEach&&this.buttons.forEach(n=>{let i=n.getAttribute(this.trimAttribute(this.config.buttonSelector)),t=document.querySelector(`[${this.trimAttribute(this.config.inputSelector)}="${i}"]`);t&&n.addEventListener&&n.addEventListener("click",()=>{this.togglePasswordAttribute(t)})})}togglePasswordAttribute(n){let t=n.getAttribute("type");n.setAttribute("type",t==="password"?"text":"password")}trimAttribute(n){return n.replace(/[\[\]]+/g,"")}}